tables.table_schema:
CREATE TABLE learned_policies (
    policy_id INT PRIMARY KEY,
    agent_name VARCHAR(255),
    policy_description TEXT
);

CREATE TABLE action_strategies (
    strategy_id INT PRIMARY KEY,
    agent_name VARCHAR(255),
    strategy_description TEXT
);

CREATE TABLE policies (
  policy_id INTEGER PRIMARY KEY,
  policy_name VARCHAR(255) NOT NULL,
  policy_description TEXT
);

CREATE TABLE actions (
  action_id INTEGER PRIMARY KEY,
  action_name VARCHAR(255) NOT NULL,
  action_value FLOAT
);

CREATE TABLE agent_strategies (
  strategy_id INTEGER PRIMARY KEY,
  agent_id INTEGER NOT NULL,
  strategy_name VARCHAR(255) NOT NULL,
  strategy_description TEXT
);

CREATE TABLE PolicyStorage (
	policy_id INT PRIMARY KEY,
	policy_name VARCHAR(255),
	action_selection_strategy VARCHAR(255),
	reward_function VARCHAR(255)
);

CREATE TABLE AgentHistory (
	history_id INT PRIMARY KEY,
	agent_id INT,
	episode INT,
	steps INT
);

CREATE TABLE TrainingSummary (
	summary_id INT PRIMARY KEY,
	agent_id INT,
	training_start TIMESTAMP,
	training_end TIMESTAMP
);
