tables.table_schema:
CREATE TABLE pipelines (
    id INT PRIMARY KEY,
    source VARCHAR(255),
    destination VARCHAR(255),
    etl_process VARCHAR(255)
);

CREATE TABLE data_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(255),
    source_type VARCHAR(255),
    source_connection VARCHAR(255)
);

CREATE TABLE source_data_table (
    id INT PRIMARY KEY,
    source_name VARCHAR,
    data_type VARCHAR,
    data_value FLOAT
);

CREATE TABLE transform_data_table (
    id INT PRIMARY KEY,
    transformed_data JSONB
);

CREATE TABLE load_data_table (
    id INT PRIMARY KEY,
    loaded_data JSONB,
    load_status VARCHAR
);

CREATE TABLE pipeline_logs_table (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    log_message TEXT
);
