tables.table_schema:
CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE sensitive_data (
  id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  password VARCHAR(50) NOT NULL
);

CREATE TABLE access_logs (
  log_id INT PRIMARY KEY,
  user_id INT NOT NULL,
  action VARCHAR(100) NOT NULL,
  timestamp TIMESTAMP NOT NULL
);

CREATE TABLE user_profiles (
  user_id INT PRIMARY KEY,
  full_name VARCHAR(100) NOT NULL,
  email VARCHAR(100) NOT NULL UNIQUE,
  role VARCHAR(50) NOT NULL
);

CREATE TABLE employees (
   employee_id INT PRIMARY KEY,
   employee_name VARCHAR(50) NOT NULL,
   employee_email VARCHAR(100) UNIQUE,
   employee_role VARCHAR(50) NOT NULL
);

CREATE TABLE departments (
   department_id INT PRIMARY KEY,
   department_name VARCHAR(50) NOT NULL,
   manager_id INT,
   CONSTRAINT fk_manager FOREIGN KEY (manager_id) REFERENCES employees(employee_id)
);

CREATE TABLE permissions (
   permission_id INT PRIMARY KEY,
   permission_name VARCHAR(50) NOT NULL,
   description TEXT,
   CONSTRAINT chk_description_length CHECK (LENGTH(description) <= 500)
);

CREATE TABLE AccessLogs (
    id INT PRIMARY KEY,
    user_id INT REFERENCES BusinessUsers(id),
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(50) NOT NULL
);

CREATE TABLE DataBackups (
    id INT PRIMARY KEY,
    backup_time TIMESTAMP NOT NULL,
    backup_location VARCHAR(100) NOT NULL,
    size_mb INT NOT NULL
);

CREATE TABLE AuditTrail (
    id INT PRIMARY KEY,
    user_id INT REFERENCES BusinessUsers(id),
    action VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);
