tables.table_schema:
CREATE TABLE building_predictions (
    prediction_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    dataset_name VARCHAR(255),
    prediction_result BOOLEAN
);

CREATE TABLE machine_learning_algorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    implemented_language VARCHAR(50),
    description TEXT
);

CREATE TABLE models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    created_at TIMESTAMP
);

CREATE TABLE datasets (
    dataset_id INT PRIMARY KEY,
    dataset_name VARCHAR(255) NOT NULL,
    file_path VARCHAR(255) NOT NULL,
    uploaded_at TIMESTAMP
);

CREATE TABLE building_data (
  building_id INT PRIMARY KEY,
  location VARCHAR(255),
  area_sqft INT,
  year_built INT
);

CREATE TABLE predictive_model (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(255),
  algorithm VARCHAR(255),
  accuracy FLOAT
);

CREATE TABLE machine_learning_data (
  data_id INT PRIMARY KEY,
  data_source VARCHAR(255),
  data_type VARCHAR(255),
  data_size INT
);

CREATE TABLE big_data_storage (
  storage_id INT PRIMARY KEY,
  storage_type VARCHAR(255),
  capacity_gb INT,
  provider VARCHAR(255)
);

CREATE TABLE ModelDataSet (
    id INT PRIMARY KEY,
    dataset_name VARCHAR(255) NOT NULL,
    dataset_description TEXT,
    dataset_size INT
);

CREATE TABLE ModelResults (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    result_date DATE,
    result_metrics TEXT
);

CREATE TABLE ModelParameters (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    param_name VARCHAR(255) NOT NULL,
    param_value FLOAT
);

CREATE TABLE ModelEvaluation (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    evaluation_date DATE,
    evaluation_metrics TEXT
);
