tables.table_schema:
CREATE TABLE agents (
    agent_id UUID PRIMARY KEY,
    agent_name VARCHAR(50) NOT NULL,
    training_status VARCHAR(20) DEFAULT 'pending'
);

CREATE TABLE tasks (
    task_id UUID PRIMARY KEY,
    agent_id UUID REFERENCES agents(agent_id),
    task_name VARCHAR(50) NOT NULL,
    task_status VARCHAR(20) DEFAULT 'pending'
);

CREATE TABLE rl_agents (
  agent_id UUID PRIMARY KEY,
  agent_name VARCHAR(50) NOT NULL,
  status VARCHAR(20) NOT NULL
);

CREATE TABLE job_logs (
  log_id UUID PRIMARY KEY,
  job_id UUID REFERENCES training_jobs(job_id),
  log_time TIMESTAMP NOT NULL,
  message TEXT
);

CREATE TABLE agent_performance (
  performance_id UUID PRIMARY KEY,
  agent_id UUID REFERENCES rl_agents(agent_id),
  training_accuracy FLOAT NOT NULL,
  training_loss FLOAT NOT NULL
);

CREATE TABLE agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(100) NOT NULL,
    creation_date DATE NOT NULL,
    training_type VARCHAR(50) NOT NULL
);

CREATE TABLE agent_logs (
    log_id INT PRIMARY KEY,
    agent_id INT NOT NULL,
    log_text TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE training_parameters (
    param_id INT PRIMARY KEY,
    agent_id INT NOT NULL,
    param_name VARCHAR(50) NOT NULL,
    param_value TEXT NOT NULL
);
