CREATE TABLE algorithms_metadata (
    algorithm_id SERIAL PRIMARY KEY,
    algorithm_name VARCHAR(255) NOT NULL,
    description TEXT,
    author VARCHAR(100) NOT NULL
);

CREATE TABLE algorithm_types (
    type_id SERIAL PRIMARY KEY,
    type_name VARCHAR(50) NOT NULL
);

CREATE TABLE algorithms (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    author VARCHAR(100) NOT NULL
);

CREATE TABLE categories (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE authors (
    id INT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE algorithm_categories (
    algorithm_id INT REFERENCES algorithms(id),
    category_id INT REFERENCES categories(id),
    PRIMARY KEY (algorithm_id, category_id)
);

