tables.table_schema:
CREATE TABLE AlgorithmRules (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(50) NOT NULL,
    rule_description TEXT,
    condition_query TEXT NOT NULL
);

CREATE TABLE Orders (
    order_id INT PRIMARY KEY,
    product_id INT NOT NULL,
    quantity INT CHECK(quantity > 0),
    CONSTRAINT fk_product_id FOREIGN KEY (product_id) REFERENCES Products(product_id)
);

CREATE TABLE Products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    price DECIMAL(10, 2) CHECK(price > 0),
    CONSTRAINT uc_product_name UNIQUE (product_name)
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE,
    CONSTRAINT ck_email CHECK (email LIKE '%@%')
);

CREATE TABLE Posts (
    post_id INT PRIMARY KEY,
    user_id INT REFERENCES Users(user_id),
    post_content VARCHAR(255) NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

CREATE TABLE Likes (
    like_id INT PRIMARY KEY,
    post_id INT REFERENCES Posts(post_id),
    user_id INT REFERENCES Users(user_id),
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

CREATE TABLE Followers (
    follower_id INT PRIMARY KEY,
    user_id INT REFERENCES Users(user_id),
    follower_user_id INT REFERENCES Users(user_id),
    follow_date DATE DEFAULT CURRENT_DATE
);
