tables.table_schema:
CREATE TABLE financial_data (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    revenue DECIMAL,
    expenses DECIMAL
);

CREATE TABLE budget_forecasts (
    id INT PRIMARY KEY,
    quarter INT,
    year INT,
    budgeted_revenue DECIMAL,
    budgeted_expenses DECIMAL
);

CREATE TABLE Department (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  manager VARCHAR(50),
  location VARCHAR(50)
);
