tables.table_schema:
CREATE TABLE FinancialRiskAnalysis (
    id INT PRIMARY KEY,
    market_data VARCHAR(255),
    historical_data TEXT,
    statistical_analysis JSON
);

CREATE TABLE market_data (
  symbol VARCHAR(255) PRIMARY KEY,
  date DATE,
  closing_price DECIMAL,
  volume INT
);

CREATE TABLE statistical_analysis (
  id SERIAL PRIMARY KEY,
  symbol VARCHAR(255) REFERENCES market_data(symbol),
  avg_price DECIMAL,
  stddev_price DECIMAL
);

CREATE TABLE risk_management (
  id SERIAL PRIMARY KEY,
  symbol VARCHAR(255) REFERENCES market_data(symbol),
  risk_score DECIMAL,
  risk_level VARCHAR(50)
);

CREATE TABLE financial_risk_management (
  stock_symbol VARCHAR(10),
  risk_level VARCHAR(20),
  recommendation VARCHAR(100)
);

CREATE TABLE historical_trends (
  date DATE,
  stock_symbol VARCHAR(10),
  trend_type VARCHAR(50)
);
