tables.table_schema:
CREATE TABLE FinancialRatios (
  id INT PRIMARY KEY,
  company_name VARCHAR(100),
  current_assets DECIMAL,
  current_liabilities DECIMAL
);

CREATE TABLE PerformanceIndicators (
  id INT PRIMARY KEY,
  company_name VARCHAR(100),
  revenue DECIMAL,
  net_income DECIMAL
);

CREATE TABLE LiquidityAnalysis (
  id INT PRIMARY KEY,
  company_name VARCHAR(100),
  current_ratio DECIMAL,
  quick_ratio DECIMAL
);

CREATE TABLE ProfitabilityMetrics (
  id INT PRIMARY KEY,
  company_name VARCHAR(100),
  gross_margin DECIMAL,
  operating_margin DECIMAL
);

CREATE TABLE EfficiencyIndicators (
  id INT PRIMARY KEY,
  company_name VARCHAR(100),
  inventory_turnover DECIMAL,
  receivables_turnover DECIMAL
);
