tables.table_schema:
CREATE TABLE Anomalies (
    anomaly_id INT PRIMARY KEY,
    transaction_id INT,
    anomaly_description VARCHAR(255),
    is_resolved BOOLEAN
);

CREATE TABLE Account (
    account_id INT PRIMARY KEY,
    account_holder VARCHAR(255),
    balance DECIMAL
);

CREATE TABLE Transaction (
    transaction_id INT PRIMARY KEY,
    account_id INT,
    transaction_amount DECIMAL,
    transaction_date TIMESTAMP
);

CREATE TABLE FraudDetection (
    detection_id INT PRIMARY KEY,
    transaction_id INT,
    detection_category VARCHAR(50),
    detection_score FLOAT
);

CREATE TABLE SuspiciousActivity (
    activity_id INT PRIMARY KEY,
    account_id INT,
    activity_description TEXT,
    activity_date TIMESTAMP
);

CREATE TABLE AnomalyAnalysis (
    anomaly_id INT PRIMARY KEY,
    detection_id INT,
    anomaly_type VARCHAR(50),
    anomaly_description TEXT
);
