tables.table_schema:
CREATE TABLE Debt_Financing (
    company_id INT,
    debt_amount DECIMAL,
    debt_term INT,
    interest_rate DECIMAL
);

CREATE TABLE Equity_Financing (
    company_id INT,
    equity_amount DECIMAL,
    shares_issued INT,
    share_price DECIMAL
);

CREATE TABLE debt_financing (
  id INT PRIMARY KEY,
  company_name VARCHAR(255),
  debt_amount DECIMAL(10, 2),
  maturity_date DATE
);

CREATE TABLE equity_financing (
  id INT PRIMARY KEY,
  company_name VARCHAR(255),
  equity_amount DECIMAL(10, 2),
  issued_shares INT
);

CREATE TABLE capital_structure (
  id INT PRIMARY KEY,
  company_name VARCHAR(255),
  debt_equity_ratio DECIMAL(5, 2),
  preferred_equity_amount DECIMAL(10, 2)
);

CREATE TABLE financial_performance (
  id INT PRIMARY KEY,
  company_name VARCHAR(255),
  revenue DECIMAL(15, 2),
  net_profit DECIMAL(15, 2)
);

CREATE TABLE CapitalStructureData (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    debt_amount DECIMAL(10, 2),
    equity_amount DECIMAL(10, 2)
);
