tables.table_schema:
CREATE TABLE Investment_Portfolio ( 
   portfolio_id UUID PRIMARY KEY, 
   investor_name VARCHAR(50), 
   investment_amount DECIMAL, 
   investment_date DATE 
);

CREATE TABLE Investment_Portsfolio (
    id INT PRIMARY KEY,
    portfolio_name VARCHAR(255),
    start_date DATE,
    end_date DATE,
    total_value DECIMAL(10, 2)
);

CREATE TABLE Portfolio_Holdings (
    id INT PRIMARY KEY,
    portfolio_id INT,
    stock_symbol VARCHAR(10),
    quantity INT,
    purchase_price DECIMAL(8, 2),
    FOREIGN KEY (portfolio_id) REFERENCES Investment_Portfolio(id)
);

CREATE TABLE Stock_Performance (
    id INT PRIMARY KEY,
    stock_symbol VARCHAR(10),
    date DATE,
    closing_price DECIMAL(8, 2)
);

CREATE TABLE Portfolio_Returns (
    id INT PRIMARY KEY,
    portfolio_id INT,
    return DECIMAL(5, 2),
    date DATE,
    FOREIGN KEY (portfolio_id) REFERENCES Investment_Portfolio(id)
);

CREATE TABLE Trend_Analysis (
    id INT PRIMARY KEY,
    trend_category VARCHAR(50),
    trend_value DECIMAL(5, 2),
    date DATE
);
