tables.table_schema:
CREATE TABLE stock_trends (
    trend_id SERIAL PRIMARY KEY,
    symbol VARCHAR(10),
    trend_description TEXT
);

CREATE TABLE investment_decisions (
    decision_id SERIAL PRIMARY KEY,
    symbol VARCHAR(10),
    action VARCHAR(10),
    amount DECIMAL(10, 2)
);

CREATE TABLE StockData (
    stock_id INT PRIMARY KEY,
    stock_name VARCHAR(255),
    stock_price DECIMAL,
    stock_volume INT
);

CREATE TABLE MarketTrends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    trend_description TEXT
);

CREATE TABLE stock_prices (
    stock_id INT PRIMARY KEY,
    stock_symbol STRING,
    price DECIMAL,
    date DATE
);

CREATE TABLE stock_analysis (
    analysis_id INT PRIMARY KEY,
    trend_id INT,
    conclusion STRING,
    analyst_id INT
);

CREATE TABLE stock_analysts (
    analyst_id INT PRIMARY KEY,
    analyst_name STRING,
    analyst_specialization STRING
);
