tables.table_schema:
CREATE TABLE Acquisition (
  acquirer_name VARCHAR,
  target_name VARCHAR,
  deal_size DECIMAL,
  deal_date DATE
);

CREATE TABLE DealParticipants (
  deal_id INT,
  participant_name VARCHAR,
  participant_role VARCHAR
);

CREATE TABLE DealMetrics (
  deal_id INT,
  deal_value DECIMAL,
  market_segment VARCHAR,
  deal_status VARCHAR
);

CREATE TABLE MarketAnalysis (
  market_segment VARCHAR,
  deal_count INT,
  total_deals_value DECIMAL
);

CREATE TABLE acquirers (
    id SERIAL PRIMARY KEY,
    acquirer_name VARCHAR(255) NOT NULL,
    industry VARCHAR(100),
    country VARCHAR(50)
);

CREATE TABLE acquirees (
    id SERIAL PRIMARY KEY,
    acquiree_name VARCHAR(255) NOT NULL,
    industry VARCHAR(100),
    country VARCHAR(50)
);

CREATE TABLE deal_dates (
    id SERIAL PRIMARY KEY,
    deal_id INTEGER REFERENCES mergers(id),
    deal_date DATE
);
