tables.table_schema:
CREATE TABLE risk_management_system (
  id INT PRIMARY KEY,
  financial_instrument VARCHAR(255),
  exposure DECIMAL,
  risk_factor DECIMAL
);

CREATE TABLE financial_instruments (
 instrument_id INT PRIMARY KEY,
 instrument_name VARCHAR(255),
 exposure_limit DECIMAL(10, 2)
);

CREATE TABLE financial_instruments (
    instrument_id INT PRIMARY KEY,
    instrument_name TEXT,
    exposure DECIMAL
);

CREATE TABLE risk_factors (
    factor_id INT PRIMARY KEY,
    factor_name TEXT,
    factor_weight DECIMAL
);

CREATE TABLE risk_management (
	id INT,
	instrument_name VARCHAR,
	exposure_amount DECIMAL,
	risk_factor DECIMAL
);

CREATE TABLE portfolios (
	portfolio_id INT,
	portfolio_name VARCHAR,
	owner VARCHAR,
	total_exposure DECIMAL
);

CREATE TABLE financial_instruments (
	instrument_id INT,
	instrument_name VARCHAR,
	risk_category VARCHAR,
	instrument_type VARCHAR
);

CREATE TABLE risk_categories (
	category_id INT,
	category_name VARCHAR
);

CREATE TABLE financial_instruments (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  instrument_type VARCHAR(50) NOT NULL,
  exposure_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE instruments_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100) NOT NULL
);
