tables.table_schema:
CREATE TABLE client_portfolios (
  client_id INT PRIMARY KEY,
  name STRING,
  email STRING,
  phone_number STRING
);

CREATE TABLE ClientPortfolio (
    id INT PRIMARY KEY,
    client_name VARCHAR(255),
    account_number VARCHAR(20),
    balance DECIMAL(10, 2)
);

CREATE TABLE Reports (
    report_id UUID PRIMARY KEY,
    client_id UUID REFERENCES Client_Portfolios(client_id),
    report_date DATE,
    report_content TEXT
);
