tables.table_schema:
CREATE TABLE bill_payments (
    id INT PRIMARY KEY,
    bill_name VARCHAR(255),
    due_date DATE,
    amount DECIMAL(10,2)
);

CREATE TABLE bill_due_dates (
    bill_id INT PRIMARY KEY,
    due_date DATE
);

CREATE TABLE bill_amounts (
    bill_id INT PRIMARY KEY,
    amount DECIMAL(10, 2)
);

CREATE TABLE users (user_id INT PRIMARY KEY,
                   user_name VARCHAR(255),
                   email VARCHAR(255));

CREATE TABLE bills (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    due_date DATE,
    amount DECIMAL
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255)
);
