);

CREATE TABLE portfolio_managers (
    manager_id INT PRIMARY KEY,
    manager_name VARCHAR(50) NOT NULL,
    manager_email VARCHAR(100) UNIQUE
);

CREATE TABLE portfolio_managers (
    manager_id INT PRIMARY KEY,
    manager_name VARCHAR(255) NOT NULL,
    manager_email VARCHAR(255) UNIQUE
);

CREATE TABLE investment_funds (
    fund_id INT PRIMARY KEY,
    fund_name VARCHAR(255) NOT NULL,
    fund_manager_id INT,
    FOREIGN KEY (fund_manager_id) REFERENCES portfolio_managers(manager_id)
);

CREATE TABLE client_accounts (
    account_id INT PRIMARY KEY,
    account_name VARCHAR(255) NOT NULL,
    account_manager_id INT,
    FOREIGN KEY (account_manager_id) REFERENCES portfolio_managers(manager_id)
);
