CREATE TABLE clients (
    client_id UUID PRIMARY KEY,
    client_name VARCHAR(50),
    client_location VARCHAR(100)
);

CREATE TABLE deals (
    deal_id UUID PRIMARY KEY,
    deal_amount DECIMAL,
    deal_date DATE,
    client_id UUID,
    FOREIGN KEY (client_id) REFERENCES clients(client_id)
);

