tables.table_schema:
CREATE TABLE depreciation_expenses (
  expense_id SERIAL PRIMARY KEY,
  asset_id UUID REFERENCES fixed_assets(asset_id),
  depreciation_amount NUMERIC,
  period DATE
);

CREATE TABLE fixed_assets (
    asset_id SERIAL PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    purchase_date DATE NOT NULL,
    purchase_cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE depreciation_methods (
    method_id SERIAL PRIMARY KEY,
    method_name VARCHAR(255) NOT NULL,
    useful_life INT NOT NULL,
    depreciation_rate DECIMAL(4, 2) NOT NULL
);

CREATE TABLE assets_info (
    asset_id SERIAL PRIMARY KEY,
    asset_description TEXT
);

CREATE TABLE depreciation_methods (
  method_id INT PRIMARY KEY,
  method_name VARCHAR(255),
  depreciation_rate DECIMAL(5, 4)
);
