tables.table_schema:
CREATE TABLE transaction_records (
    id SERIAL PRIMARY KEY,
    transaction_date DATE NOT NULL,
    transaction_description TEXT,
    amount DECIMAL NOT NULL
);

CREATE TABLE bank_statements (
    id SERIAL PRIMARY KEY,
    statement_date DATE NOT NULL,
    statement_balance DECIMAL NOT NULL
);

CREATE TABLE BankStatements (
    statement_id INT PRIMARY KEY,
    account_id INT,
    statement_date DATE,
    closing_balance DECIMAL
);

CREATE TABLE bank_transactions (
    transaction_id SERIAL PRIMARY KEY,
    transaction_date DATE,
    account_number VARCHAR(10),
    transaction_amount DECIMAL(10, 2)
);

CREATE TABLE discrepancies (
    discrepancy_id SERIAL PRIMARY KEY,
    reconciliation_id INT REFERENCES reconciled_transactions(reconciliation_id),
    discrepancy_type VARCHAR(50),
    discrepancy_notes TEXT
);

CREATE TABLE Bank_Statements (
    statement_id INT PRIMARY KEY,
    statement_date DATE,
    statement_amount DECIMAL
);

CREATE TABLE Recorded_Transactions (
    transaction_id INT PRIMARY KEY,
    transaction_date DATE,
    transaction_amount DECIMAL,
    description TEXT
);

CREATE TABLE Reconciliation_Log (
    log_id INT PRIMARY KEY,
    statement_id INT,
    transaction_id INT,
    status VARCHAR(10)
);

CREATE TABLE Reconciliation_Errors (
    error_id INT PRIMARY KEY,
    log_id INT,
    error_description TEXT
);

CREATE TABLE Reconciliation_Audit (
    audit_id INT PRIMARY KEY,
    log_id INT,
    user_id INT,
    timestamp TIMESTAMP
);
