tables.table_schema:
CREATE TABLE market_data (
    symbol TEXT,
    price NUMERIC,
    volume INT,
    last_updated TIMESTAMP
);

CREATE TABLE client_investments (
    client_id INT,
    symbol TEXT,
    quantity INT,
    investment_date DATE
);

CREATE TABLE MarketData (
    date DATE,
    stock_symbol VARCHAR,
    close_price DECIMAL,
    volume INT
);

CREATE TABLE Clients (
    client_id INT,
    client_name VARCHAR,
    account_balance DECIMAL
);

CREATE TABLE Investment_Firms (
    firm_id INT PRIMARY KEY,
    firm_name VARCHAR(50),
    location VARCHAR(100),
    founding_date DATE
);
