tables.table_schema:
CREATE TABLE financial_products_performance (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    product_type VARCHAR(50),
    performance_data JSON
);

CREATE TABLE historical_performance_data (
  product_id INT,
  product_name VARCHAR(255),
  performance_date DATE,
  performance_return DECIMAL(10, 2)
);

CREATE TABLE historical_performance (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    performance_date DATE,
    performance_value DECIMAL
);

CREATE TABLE categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    created_at TIMESTAMPTZ
);

CREATE TABLE Product (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    category VARCHAR(50)
);

CREATE TABLE FinancialAdviser (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE Client (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(255),
    address TEXT
);
