tables.table_schema:
CREATE TABLE ClientProfiles (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(255),
    investment_preferences TEXT
);

CREATE TABLE clients (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(50),
    investment_preferences VARCHAR(100)
);

CREATE TABLE investments (
    investment_id INT PRIMARY KEY,
    investment_name VARCHAR(50),
    investment_type VARCHAR(50),
    investment_amount DECIMAL(10, 2)
);

CREATE TABLE ReportTemplates (
    template_id INT PRIMARY KEY,
    template_name VARCHAR(50),
    template_content TEXT
);

CREATE TABLE ClientPreferences (
    client_id INT PRIMARY KEY,
    risk_tolerance VARCHAR(50),
    investment_horizon INT,
    investment_strategy VARCHAR(50)
);

CREATE TABLE CustomizedReports (
    report_id INT PRIMARY KEY,
    client_id INT,
    report_date DATE,
    report_content TEXT
);

CREATE TABLE InvestmentStrategy (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(50),
    strategy_description TEXT
);
