tables.table_schema:
CREATE TABLE portfolios (
    id UUID PRIMARY KEY,
    client_id UUID NOT NULL,
    portfolio_name VARCHAR(255) NOT NULL,
    assets JSONB NOT NULL
);

CREATE TABLE rebalancing_criteria (
    id UUID PRIMARY KEY,
    client_id UUID NOT NULL,
    criteria_name VARCHAR(255) NOT NULL,
    criteria_details TEXT
);

CREATE TABLE rebalancing_logs (
    id INT PRIMARY KEY,
    portfolio_id INT,
    rebalanced_on TIMESTAMP,
    rebalance_details JSON
);

CREATE TABLE portfolios(
  id UUID PRIMARY KEY,
  client_id UUID,
  name TEXT,
  description TEXT
);

CREATE TABLE assets(
  id UUID PRIMARY KEY,
  symbol TEXT,
  name TEXT,
  asset_class TEXT
);

CREATE TABLE criteria(
  id UUID PRIMARY KEY,
  name TEXT,
  condition TEXT,
  weight DOUBLE
);
