tables.table_schema:
CREATE TABLE portfolios (
    portfolio_id SERIAL PRIMARY KEY,
    portfolio_name VARCHAR(255) NOT NULL,
    risk_exposure DECIMAL
);

CREATE TABLE investment_portfolio (
  id INT PRIMARY KEY,
  portfolio_name VARCHAR(255),
  total_value DECIMAL(10, 2),
  risk_score FLOAT
);

CREATE TABLE risk_exposure (
  id INT PRIMARY KEY,
  asset_id INT,
  risk_factor FLOAT,
  FOREIGN KEY (asset_id) REFERENCES investment_assets(asset_id)
);

CREATE TABLE portfolio ( 
    portfolio_id UUID PRIMARY KEY, 
    portfolio_name VARCHAR(255) NOT NULL, 
    total_value DECIMAL(10, 2) NOT NULL, 
    risk_level VARCHAR(50) 
);

CREATE TABLE investment ( 
    investment_id UUID PRIMARY KEY, 
    investment_name VARCHAR(255) NOT NULL, 
    amount_invested DECIMAL(10, 2) NOT NULL, 
    investment_date DATE 
);

CREATE TABLE asset ( 
    asset_id UUID PRIMARY KEY, 
    asset_name VARCHAR(255) NOT NULL, 
    asset_type VARCHAR(50), 
    asset_value DECIMAL(10, 2) NOT NULL 
);

CREATE TABLE exposure ( 
    exposure_id UUID PRIMARY KEY, 
    portfolio_id UUID REFERENCES portfolio(portfolio_id), 
    asset_id UUID REFERENCES asset(asset_id), 
    exposure_amount DECIMAL(10, 2) NOT NULL 
);

CREATE TABLE investment_portfolios (
    portfolio_id INT PRIMARY KEY,
    portfolio_name TEXT,
    risk_score FLOAT
);

CREATE TABLE asset_classes (
    class_id INT PRIMARY KEY,
    class_name TEXT
);

CREATE TABLE asset_allocations (
    allocation_id INT PRIMARY KEY,
    asset_id INT REFERENCES portfolio_assets(asset_id),
    class_id INT REFERENCES asset_classes(class_id),
    percentage FLOAT
);

CREATE TABLE risk_metrics (
    metric_id INT PRIMARY KEY,
    date DATE,
    portfolio_id INT REFERENCES investment_portfolios(portfolio_id),
    exposure FLOAT
);
