tables.table_schema:
CREATE TABLE stress_tests (
  test_id INT PRIMARY KEY,
  test_name VARCHAR(50),
  test_date DATE,
  num_iterations INT
);

CREATE TABLE StressTests (
    test_id INT PRIMARY KEY,
    test_name VARCHAR(255) NOT NULL,
    test_description TEXT,
    test_date DATE
);

CREATE TABLE TestScenarios (
    scenario_id INT PRIMARY KEY,
    scenario_name VARCHAR(255) NOT NULL,
    scenario_description TEXT,
    scenario_result DECIMAL(10, 2)
);

CREATE TABLE RiskFactors (
    factor_id INT PRIMARY KEY,
    factor_name VARCHAR(255) NOT NULL,
    factor_weight DECIMAL(5, 2)
);
