tables.table_schema:
CREATE TABLE regulatory_compliance_data (
    id UUID PRIMARY KEY,
    company_name VARCHAR(255),
    regulation_type VARCHAR(100),
    compliance_status BOOLEAN
);

CREATE TABLE compliance_data (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    risk_level INT,
    report_date DATE
);

CREATE TABLE regulations (
    reg_id INT PRIMARY KEY,
    reg_name VARCHAR(50),
    reg_description TEXT
);

CREATE TABLE monitoring_records (
    record_id INT PRIMARY KEY,
    compliance_id INT,
    regulation_id INT,
    record_date TIMESTAMP,
    compliance_status VARCHAR(10)
);

CREATE TABLE RegulatoryEntity (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(255) NOT NULL,
    entity_type VARCHAR(50) NOT NULL
);

CREATE TABLE ComplianceArea (
    area_id INT PRIMARY KEY,
    area_name VARCHAR(255) NOT NULL,
    area_description TEXT
);

CREATE TABLE ComplianceReports (
    report_id INT PRIMARY KEY,
    entity_id INT NOT NULL,
    report_date DATE,
    report_content TEXT
);

CREATE TABLE regulatory_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    agency_type VARCHAR(255),
    agency_description TEXT
);

CREATE TABLE risk_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    category_description TEXT
);
