tables.table_schema:
CREATE TABLE payment_history (payment_id UUID PRIMARY KEY,
               loan_id UUID,
               payment_date TIMESTAMP,
               payment_amount DECIMAL,
               remaining_balance DECIMAL
);

CREATE TABLE payment_plan (plan_id UUID PRIMARY KEY,
               loan_id UUID,
               installment_number INT,
               installment_date TIMESTAMP,
               installment_amount DECIMAL
);

CREATE TABLE payment_schedule (
    payment_id INT PRIMARY KEY,
    loan_id INT,
    payment_date DATE,
    amount_paid DECIMAL
);

CREATE TABLE interest_schedule (
    loan_id INT,
    payment_date DATE,
    daily_interest DECIMAL,
    PRIMARY KEY (loan_id, payment_date)
);

CREATE TABLE remaining_balance (
    loan_id INT,
    payment_date DATE,
    balance DECIMAL,
    PRIMARY KEY (loan_id, payment_date)
);

CREATE TABLE total_paid (
    loan_id INT PRIMARY KEY,
    total_amount DECIMAL
);
