tables.table_schema:
CREATE TABLE tax_info ( 
  tax_id INT PRIMARY KEY, 
  taxpayer_name VARCHAR(50), 
  income DECIMAL(10, 2), 
  tax_rate DECIMAL(5, 2) 
);

CREATE TABLE TaxPayers (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL
);

CREATE TABLE TaxReports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    taxpayer_id INT,
    tax_amount DECIMAL
);

CREATE TABLE tax_payers (
    tax_payer_id INT PRIMARY KEY,
    tax_payer_name VARCHAR(50) NOT NULL,
    tax_payer_address VARCHAR(100) NOT NULL,
    tax_payer_income DECIMAL(10, 2) NOT NULL
);

CREATE TABLE tax_forms (
    form_id INT PRIMARY KEY,
    tax_payer_id INT REFERENCES tax_payers(tax_payer_id),
    form_type VARCHAR(10) NOT NULL,
    form_year INT NOT NULL
);

CREATE TABLE tax_payments (
    payment_id INT PRIMARY KEY,
    tax_payer_id INT REFERENCES tax_payers(tax_payer_id),
    payment_date DATE NOT NULL,
    payment_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE tax_deductions (
    deduction_id INT PRIMARY KEY,
    tax_payer_id INT REFERENCES tax_payers(tax_payer_id),
    deduction_type VARCHAR(50) NOT NULL,
    deduction_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE tax_reports (
    report_id INT PRIMARY KEY,
    tax_payer_id INT REFERENCES tax_payers(tax_payer_id),
    report_year INT NOT NULL,
    report_comments VARCHAR(255)
);
