tables.table_schema:
CREATE TABLE CashFlowStatements (
    statement_id INT PRIMARY KEY,
    report_date DATE,
    cash_inflow DECIMAL,
    cash_outflow DECIMAL
);

CREATE TABLE monitoring_cash_flow (
    cash_flow_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    cash_in DECIMAL,
    cash_out DECIMAL
);
CREATE TABLE financial_forecasting (
    forecast_id UUID PRIMARY KEY,
    date DATE,
    revenue DECIMAL,
    expenses DECIMAL
);

CREATE TABLE cash_flow_statement (
    statement_id INT PRIMARY KEY,
    date DATE,
    income DECIMAL,
    expenses DECIMAL
);

CREATE TABLE cash_flow_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL
);

CREATE TABLE cash_flow_forecast (
    forecast_id INT PRIMARY KEY,
    date DATE,
    projected_income DECIMAL,
    projected_expenses DECIMAL
);

CREATE TABLE cash_flow_statement (
    statement_id INT PRIMARY KEY,
    amount DECIMAL,
    date DATE
);

CREATE TABLE cash_flow_category (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE cash_flow_classification (
    classification_id INT PRIMARY KEY,
    classification_name VARCHAR(50)
);
