tables.table_schema:
CREATE TABLE market_data (
  symbol VARCHAR PRIMARY KEY,
  price DECIMAL,
  volume INT
);

CREATE TABLE analysts (
  analyst_id SERIAL PRIMARY KEY,
  name VARCHAR,
  position VARCHAR
);

CREATE TABLE market_participants (
  participant_id INT PRIMARY KEY,
  name VARCHAR(50),
  country VARCHAR(50),
  industry VARCHAR(50)
);

CREATE TABLE Securities (
    symbol STRING PRIMARY KEY,
    company_name STRING,
    price DECIMAL,
    volume INT
);

CREATE TABLE Traders (
    id INT PRIMARY KEY,
    name STRING,
    account_balance DECIMAL
);

CREATE TABLE MarketNews (
    news_id INT PRIMARY KEY,
    date DATE,
    headline STRING,
    content TEXT
);
