tables.table_schema:
CREATE TABLE projections (
  projection_id INT PRIMARY KEY,
  project_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE FinancialProjections (
    projection_id UUID PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    projected_revenue DECIMAL NOT NULL,
    projected_expenses DECIMAL NOT NULL
);

CREATE TABLE ForecastComparisons (
    comparison_id UUID PRIMARY KEY,
    projection_id UUID REFERENCES FinancialProjections(projection_id),
    forecast_name VARCHAR(255) NOT NULL,
    comparison_date DATE NOT NULL
);

CREATE TABLE FinancialTrends (
    trend_id UUID PRIMARY KEY,
    projection_id UUID REFERENCES FinancialProjections(projection_id),
    trend_type VARCHAR(50) NOT NULL,
    trend_value DECIMAL NOT NULL
);

CREATE TABLE ForecastScenarios (
    scenario_id UUID PRIMARY KEY,
    projection_id UUID REFERENCES FinancialProjections(projection_id),
    scenario_name VARCHAR(255) NOT NULL,
    scenario_description TEXT
);
