tables.table_schema:
CREATE TABLE stock_companies (
    company_name VARCHAR(50),
    stock_symbol VARCHAR(10),
    industry VARCHAR(50),
    exchange VARCHAR(10)
);

CREATE TABLE stock_symbols (
	symbol_id INT,
	symbol_name VARCHAR(50)
);

CREATE TABLE stock_investors (
	investor_id INT,
	investor_name VARCHAR(50),
	investor_type VARCHAR(50)
);

CREATE TABLE historical_stock_prices (
    stock_symbol VARCHAR(10) PRIMARY KEY,
    date DATE,
    opening_price DECIMAL(
        10,
        2
    ),
    closing_price DECIMAL(
        10,
        2
    ),
    high_price DECIMAL(
        10,
        2
    ),
    low_price DECIMAL(
        10,
        2
    )
);

CREATE TABLE stock_companies (
    company_id SERIAL PRIMARY KEY,
    company_name VARCHAR(50) NOT NULL,
    sector VARCHAR(30)
);

CREATE TABLE stock_exchange (
    exchange_id SERIAL PRIMARY KEY,
    exchange_name VARCHAR(50) NOT NULL,
    country VARCHAR(30)
);

CREATE TABLE stock_analysis (
    analysis_id SERIAL PRIMARY KEY,
    stock_symbol VARCHAR(10),
    analysis_date DATE,
    analyst VARCHAR(50),
    rating VARCHAR(10)
);

CREATE TABLE StockSymbols (
    symbol_id INT PRIMARY KEY,
    stock_name VARCHAR,
    symbol VARCHAR
);

CREATE TABLE StockCompanies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR,
    industry VARCHAR
);

CREATE TABLE StockExchange (
    exchange_id INT PRIMARY KEY,
    exchange_name VARCHAR,
    country VARCHAR
);

CREATE TABLE StockAnalysts (
    analyst_id INT PRIMARY KEY,
    analyst_name VARCHAR,
    rating DECIMAL
);
