);

CREATE TABLE portfolio (
    portfolio_id INT PRIMARY KEY,
    investor_id INT,
    investment_amount DECIMAL,
    return DECIMAL
);

CREATE TABLE investor (
    investor_id INT PRIMARY KEY,
    investor_name VARCHAR(255)
);

CREATE TABLE Portfolio (
    portfolio_id INT PRIMARY KEY,
    portfolio_name TEXT
);

CREATE TABLE Investment (
    investment_id INT PRIMARY KEY,
    portfolio_id INT,
    investment_name TEXT,
    amount DECIMAL(10,2),
    return DECIMAL(10,2)
);

CREATE TABLE PortfolioComposition (
    composition_id INT PRIMARY KEY,
    portfolio_id INT,
    investment_id INT,
    quantity INT
);

CREATE TABLE ROI (
    roi_id INT PRIMARY KEY,
    portfolio_id INT,
    avg_return DECIMAL(10,2)
);

CREATE TABLE portfolio (
    portfolio_id INT PRIMARY KEY,
    portfolio_name VARCHAR(255),
    creation_date DATE
);

CREATE TABLE transaction (
    transaction_id INT PRIMARY KEY,
    investment_id INT,
    transaction_date DATE,
    transaction_amount DECIMAL(10,2)
);

CREATE TABLE portfolio_investment (
    portfolio_id INT,
    investment_id INT
);

CREATE TABLE portfolio_roi (
    portfolio_id INT,
    roi DECIMAL(10,2)
);
