tables.table_schema:
CREATE TABLE investor_sentiment (
  investor_id INT PRIMARY KEY,
  social_media_handle VARCHAR(255),
  sentiment_score DECIMAL(5,2),
  sentiment_date DATE
);

CREATE TABLE social_media_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(100) UNIQUE,
    followers_count INT,
    join_date DATE
);

CREATE TABLE sentiment_categories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(100) UNIQUE
);

CREATE TABLE sentiment_mapping (
    map_id SERIAL PRIMARY KEY,
    post_id INT REFERENCES investor_sentiment_data(id),
    category_id INT REFERENCES sentiment_categories(category_id)
);

CREATE TABLE InvestorsSentiment (
    id INT PRIMARY KEY,
    investor_name VARCHAR(255),
    social_media_platform VARCHAR(255),
    sentiment_score FLOAT
);

CREATE TABLE Hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_name VARCHAR(255)
);

CREATE TABLE PostHashtags (
    post_id INT,
    hashtag_id INT,
    FOREIGN KEY (post_id) REFERENCES InvestorsPosts(post_id),
    FOREIGN KEY (hashtag_id) REFERENCES Hashtags(hashtag_id)
);

CREATE TABLE SentimentAnalysis (
    post_id INT,
    sentiment_score FLOAT,
    FOREIGN KEY (post_id) REFERENCES InvestorsPosts(post_id)
);
