tables.table_schema:
CREATE TABLE top_performing_stocks (
    stock_symbol VARCHAR(10),
    stock_name VARCHAR(50),
    performance_score FLOAT
);

CREATE TABLE historical_performance_data (
    stock_id INT,
    performance_date DATE,
    closing_price DECIMAL(10, 2)
);

CREATE TABLE stocks ( 
	stock_id INT PRIMARY KEY, 
	stock_name VARCHAR(255) NOT NULL, 
	stock_price DECIMAL(10,2) NOT NULL, 
	stock_performance DECIMAL(5,2) NOT NULL 
);

CREATE TABLE Stock (
    stock_id INT PRIMARY KEY,
    stock_name VARCHAR(50) NOT NULL,
    stock_symbol VARCHAR(10) UNIQUE NOT NULL
);

CREATE TABLE StockPerformance (
    performance_id INT PRIMARY KEY,
    stock_id INT REFERENCES Stock(stock_id),
    performance_date DATE,
    performance_value DECIMAL(10,2)
);

CREATE TABLE StockRanking (
    ranking_id INT PRIMARY KEY,
    stock_id INT REFERENCES Stock(stock_id),
    ranking_position INT,
    ranking_date DATE
);

CREATE TABLE StockView (
    view_id INT PRIMARY KEY,
    stock_id INT REFERENCES Stock(stock_id),
    view_name VARCHAR(50) NOT NULL,
    view_description TEXT
);
