tables.table_schema:
CREATE TABLE users (
  user_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  balance DECIMAL
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  balance FLOAT
);

CREATE TABLE cryptocurrency_wallets (
  wallet_id INT PRIMARY KEY,
  user_id INT,
  address VARCHAR(255),
  balance FLOAT
);

CREATE TABLE user (
  id UUID PRIMARY KEY,
  name VARCHAR(100),
  email VARCHAR(100),
  balance DECIMAL
);

CREATE TABLE wallet (
  id UUID PRIMARY KEY,
  user_id UUID,
  balance DECIMAL,
  currency VARCHAR(10)
);

CREATE TABLE currency_exchange (
  id UUID PRIMARY KEY,
  from_currency VARCHAR(10),
  to_currency VARCHAR(10),
  exchange_rate DECIMAL
);

CREATE TABLE IF NOT EXISTS users (
    id INT PRIMARY KEY,
    username TEXT,
    email TEXT,
    balance DECIMAL
);

CREATE TABLE IF NOT EXISTS wallets (
    wallet_id INT PRIMARY KEY,
    user_id INT,
    currency TEXT,
    balance DECIMAL
);

CREATE TABLE IF NOT EXISTS currencies (
    currency_id INT PRIMARY KEY,
    currency_name TEXT
);
