tables.table_schema:
CREATE TABLE historical_market_data (
  date DATE,
  symbol VARCHAR,
  close_price DECIMAL,
  volume INT
);

CREATE TABLE trend_analysis (
  symbol VARCHAR,
  trend VARCHAR,
  prediction DECIMAL
);

CREATE TABLE forecasts (
  forecast_date DATE,
  symbol VARCHAR(10),
  predicted_price DECIMAL
);

CREATE TABLE trend_analysis (
  analysis_date DATE,
  symbol VARCHAR(10),
  trend_type VARCHAR(20)
);

CREATE TABLE MarketIndices (
    date DATE,
    index_name VARCHAR(50),
    value DECIMAL(10, 2),
    change_percent DECIMAL(5, 2)
);

CREATE TABLE UserActivity (
    user_id INT,
    date DATE,
    action VARCHAR(50)
);
