tables.table_schema:
CREATE TABLE compliance_platform (
    id SERIAL PRIMARY KEY,
    regulation VARCHAR(100) NOT NULL,
    audit_date DATE,
    audit_status VARCHAR(50) CHECK (audit_status IN ('Passed', 'Failed', 'Pending'))
);

CREATE TABLE regulatory_requirements (
  requirement_id INT PRIMARY KEY,
  requirement_name VARCHAR(255) NOT NULL,
  deadline DATE
);

CREATE TABLE audits (
  audit_id INT PRIMARY KEY,
  audit_date DATE NOT NULL,
  auditor_name VARCHAR(255)
);

CREATE TABLE compliance_checks (
  check_id INT PRIMARY KEY,
  requirement_id INT REFERENCES regulatory_requirements(requirement_id),
  audit_id INT REFERENCES audits(audit_id),
  result VARCHAR(50) NOT NULL
);

CREATE TABLE RegulatoryRequirements (
  requirement_id INT PRIMARY KEY,
  requirement_name TEXT NOT NULL,
  requirement_description TEXT
);

CREATE TABLE Audits (
  audit_id INT PRIMARY KEY,
  audit_date DATE,
  audit_type TEXT NOT NULL,
  result TEXT
);

CREATE TABLE ComplianceOfficers (
  officer_id INT PRIMARY KEY,
  officer_name TEXT NOT NULL,
  company_name TEXT
);
