    id SERIAL PRIMARY KEY,
    thermostat_id INT NOT NULL,
    temperature FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE ThermostatLocation (
    id SERIAL PRIMARY KEY,
    thermostat_id INT NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE ThermostatModel (
    id SERIAL PRIMARY KEY,
    model_id INT NOT NULL,
    model_name VARCHAR(255) NOT NULL,
    manufacturer VARCHAR(255) NOT NULL
);

CREATE TABLE ThermostatHealth (
    id SERIAL PRIMARY KEY,
    thermostat_id INT NOT NULL,
    status VARCHAR(50) NOT NULL,
    battery_level FLOAT NOT NULL
);

CREATE TABLE smart_thermostat_data (
    thermostat_id SERIAL PRIMARY KEY,
    temperature FLOAT,
    timestamp TIMESTAMP,
    location VARCHAR
);

CREATE TABLE thermostat_settings (
    thermostat_id SERIAL PRIMARY KEY,
    target_temperature FLOAT,
    heating_enabled BOOLEAN,
    cooling_enabled BOOLEAN
);

CREATE TABLE thermostat_errors (
    error_id SERIAL PRIMARY KEY,
    thermostat_id INTEGER,
    error_message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE thermostat_usage (
    usage_id SERIAL PRIMARY KEY,
    thermostat_id INTEGER,
    usage_duration INTERVAL,
    timestamp TIMESTAMP
);

CREATE TABLE thermostat_locations (
    location_id SERIAL PRIMARY KEY,
    location_name VARCHAR,
    latitude DECIMAL,
    longitude DECIMAL
);
