tables.table_schema:
CREATE TABLE ProductionEfficiency (
    efficiency_id INT PRIMARY KEY,
    process_id INT,
    efficiency_rating DECIMAL(5,2),
    efficiency_date DATE
);

CREATE TABLE ProductionLineProcesses ( 
  process_id INT PRIMARY KEY, 
  process_name VARCHAR(255), 
  start_time TIMESTAMP, 
  end_time TIMESTAMP 
);

CREATE TABLE ProductionEfficiency ( 
  efficiency_id INT PRIMARY KEY, 
  process_id INT, 
  efficiency_percentage DECIMAL(5,2), 
  efficiency_date DATE 
);

CREATE TABLE Employees ( 
  employee_id INT PRIMARY KEY, 
  employee_name VARCHAR(255), 
  department VARCHAR(50), 
  hire_date DATE 
);

CREATE TABLE ProductionLine (
    line_id INT PRIMARY KEY,
    line_name VARCHAR(50) NOT NULL,
    line_capacity INT
);

CREATE TABLE Product (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    product_category VARCHAR(50),
    unit_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE ProductionStatus (
    status_id INT PRIMARY KEY,
    status_name VARCHAR(50) NOT NULL,
    status_description TEXT
);

CREATE TABLE ProductionEfficiency (
  efficiency_id INT PRIMARY KEY,
  process_id INT REFERENCES ProductionLineProcesses(process_id),
  efficiency_percentage DECIMAL(5,2) NOT NULL,
  efficiency_date DATE NOT NULL
);

CREATE TABLE Employees (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR(50) NOT NULL,
  department VARCHAR(50) NOT NULL
);

CREATE TABLE Tasks (
  task_id INT PRIMARY KEY,
  task_name VARCHAR(50) NOT NULL,
  task_description TEXT
);

CREATE TABLE TaskAssignments (
  assignment_id INT PRIMARY KEY,
  task_id INT REFERENCES Tasks(task_id),
  employee_id INT REFERENCES Employees(employee_id),
  assigned_date DATE NOT NULL
);
