tables.table_schema:
CREATE TABLE energy_consumption_data (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    energy_consumption DECIMAL,
    date_recorded TIMESTAMP
);

CREATE TABLE equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    energy_consumption DOUBLE
);

CREATE TABLE equipment (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(50),
  equipment_type VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE Industrial_Equipment (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(50),
  location_id INT,
  energy_consumed DECIMAL(10,2)
);

CREATE TABLE Equipment_Location (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(50),
  city VARCHAR(50),
  state VARCHAR(50)
);

CREATE TABLE equipment (
  id INT PRIMARY KEY,
  equipment_name VARCHAR(50) NOT NULL,
  manufacturer VARCHAR(50) NOT NULL,
  energy_consumption DECIMAL(10, 2) NOT NULL
);

CREATE TABLE maintenance_logs (
  log_id INT PRIMARY KEY,
  equipment_id INT,
  maintenance_date DATE NOT NULL,
  description TEXT
);

CREATE TABLE energy_consumption_data (
  data_id INT PRIMARY KEY,
  equipment_id INT,
  consumption_date DATE NOT NULL,
  energy_consumed DECIMAL(10, 2) NOT NULL
);

CREATE TABLE energy_cost_data (
  cost_id INT PRIMARY KEY,
  equipment_id INT,
  cost_date DATE NOT NULL,
  energy_cost DECIMAL(10, 2) NOT NULL
);

CREATE TABLE energy_usage_stats (
  stats_id INT PRIMARY KEY,
  equipment_id INT,
  month_year DATE NOT NULL,
  energy_usage DECIMAL(10, 2) NOT NULL
);
