tables.table_schema:
CREATE TABLE robotic_systems_data (
    id INT NOT NULL PRIMARY KEY,
    system_name VARCHAR(50) NOT NULL,
    capture_timestamp TIMESTAMP NOT NULL,
    performance_metric FLOAT NOT NULL
);

CREATE TABLE system_component_data (
    component_id INT NOT NULL PRIMARY KEY,
    system_id INT NOT NULL,
    component_name VARCHAR(50) NOT NULL,
    component_status VARCHAR(20) NOT NULL
);

CREATE TABLE workflow_optimization (
    workflow_id INT NOT NULL PRIMARY KEY,
    system_id INT NOT NULL,
    optimization_status VARCHAR(20) NOT NULL,
    optimization_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE RoboticSystems (
    robotic_system_id INT PRIMARY KEY,
    system_name VARCHAR(50),
    production_capacity INT,
    installation_date DATE
);

CREATE TABLE WorkflowOptimization (
    optimization_id INT PRIMARY KEY,
    metric_id INT,
    optimization_strategy TEXT,
    improvement_percentage FLOAT
);

CREATE TABLE MaintenanceLogs (
    log_id INT PRIMARY KEY,
    robotic_system_id INT,
    maintenance_date DATE,
    maintenance_description TEXT
);

CREATE TABLE Robot (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(255),
    robot_type VARCHAR(50),
    manufacturing_date DATE
);
