tables.table_schema:
CREATE TABLE driver_preferences (
    driver_id INT PRIMARY KEY,
    music_genre VARCHAR(255),
    playlist_id INT
);

CREATE TABLE playlist (
    playlist_id INT PRIMARY KEY,
    playlist_name VARCHAR(255),
    playlist_duration INT
);

CREATE TABLE driver_preferences (
    driver_id UUID PRIMARY KEY,
    music_preference TEXT,
    podcast_preference TEXT,
    volume_level INT
);

CREATE TABLE drivers (
    driver_id INT PRIMARY KEY,
    driver_name TEXT
);

CREATE TABLE music_preferences (
    driver_id INT,
    music_genre TEXT,
    volume_level INT,
    FOREIGN KEY (driver_id) REFERENCES drivers(driver_id)
);

CREATE TABLE radio_stations (
    driver_id INT,
    station_name TEXT,
    frequency FLOAT,
    FOREIGN KEY (driver_id) REFERENCES drivers(driver_id)
);

CREATE TABLE drivers_preferences (
    id INT PRIMARY KEY,
    driver_name VARCHAR(50) NOT NULL,
    favorite_radio_station VARCHAR(50),
    playlist_id INT
);
CREATE TABLE playlists (
    playlist_id INT PRIMARY KEY,
    driver_id INT NOT NULL,
    playlist_name VARCHAR(50) NOT NULL,
    song_count INT
);
CREATE TABLE songs (
    song_id INT PRIMARY KEY,
    song_name VARCHAR(50) NOT NULL,
    artist_name VARCHAR(50) NOT NULL,
    album_name VARCHAR(50),
    duration_minutes FLOAT
);
CREATE TABLE radio_stations (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(50) NOT NULL,
    frequency VARCHAR(20) NOT NULL,
    genre VARCHAR(50)
);
CREATE TABLE driver_cars (
    driver_id INT PRIMARY KEY,
    car_make VARCHAR(50) NOT NULL,
    car_model VARCHAR(50) NOT NULL,
    car_year INT
);
