tables.table_schema:
CREATE TABLE TrafficPatterns (
  id UUID PRIMARY KEY,
  route_id UUID,
  traffic_date DATE,
  congestion_level INT
);

CREATE TABLE RouteDetails (
  route_id UUID PRIMARY KEY,
  start_location VARCHAR,
  end_location VARCHAR,
  distance FLOAT
);

CREATE TABLE DailyCongestion (
  id UUID PRIMARY KEY,
  traffic_date DATE,
  congestion_level INT,
  route_id UUID
);

CREATE TABLE TrafficIncidents (
  incident_id UUID PRIMARY KEY,
  location VARCHAR,
  incident_type VARCHAR,
  incident_date DATE
);

CREATE TABLE CongestionLevel (
  congestion_id INT PRIMARY KEY,
  congestion_level INT,
  congestion_description TEXT
);

CREATE TABLE Weather (
  weather_id INT PRIMARY KEY,
  route_id INT,
  weather_condition VARCHAR(50),
  temperature FLOAT
);
