tables.table_schema:
CREATE TABLE security_alerts (
    id INT PRIMARY KEY,
    camera_id INT,
    alert_type VARCHAR(50),
    alert_timestamp TIMESTAMP
);

CREATE TABLE smart_home_notifications (
    id INT PRIMARY KEY,
    sensor_id INT,
    notification_message TEXT,
    notification_timestamp TIMESTAMP
);

CREATE TABLE notifications (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    device_id UUID,
    message TEXT
);

CREATE TABLE smart_home_cameras (
    id UUID PRIMARY KEY,
    location VARCHAR,
    resolution VARCHAR,
    status VARCHAR
);

CREATE TABLE cameras (
  camera_id SERIAL PRIMARY KEY,
  camera_name VARCHAR(50),
  location VARCHAR(100),
  is_active BOOLEAN
);

CREATE TABLE users (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  phone_number VARCHAR(20)
);
