tables.table_schema:
CREATE TABLE healthcare_expenses (
    expense_id INT PRIMARY KEY,
    date DATE,
    expense_amount DECIMAL(10, 2),
    description TEXT
);

CREATE TABLE reimbursement (
    reimbursement_id INT PRIMARY KEY,
    claim_id INT,
    reimbursement_amount DECIMAL(10, 2),
    date_processed DATE
);

CREATE TABLE healthcare_expenses (
  id INT PRIMARY KEY,
  patient_id INT,
  expense_date DATE,
  expense_amount DECIMAL(10, 2)
);

CREATE TABLE insurance_claims (
  claim_id INT PRIMARY KEY,
  patient_id INT,
  claim_date DATE,
  claim_amount DECIMAL(10, 2)
);

CREATE TABLE billing_records (
  record_id INT PRIMARY KEY,
  claim_id INT,
  record_date DATE,
  billed_amount DECIMAL(10, 2)
);

CREATE TABLE reimbursement_records (
  reimbursement_id INT PRIMARY KEY,
  claim_id INT,
  reimbursement_date DATE,
  reimbursed_amount DECIMAL(10, 2)
);
