tables.table_schema:
CREATE TABLE feedback (
    feedback_id UUID PRIMARY KEY,
    patient_id UUID REFERENCES patients(patient_id),
    feedback_text TEXT,
    rating INT
);

CREATE TABLE feedback (
    feedback_id SERIAL PRIMARY KEY,
    patient_id INT REFERENCES patients(patient_id),
    feedback_date DATE,
    feedback_message TEXT
);

CREATE TABLE PatientSurveyFeedback (
    survey_id INT PRIMARY KEY,
    patient_id INT,
    survey_date DATE,
    survey_response TEXT
);

CREATE TABLE CareQualityMetrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    metric_value FLOAT,
    metric_date DATE
);

CREATE TABLE TreatmentHistory (
    treatment_id INT PRIMARY KEY,
    patient_id INT,
    treatment_type VARCHAR(50),
    treatment_date DATE
);

CREATE TABLE CareProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50),
    provider_specialty VARCHAR(50)
);

CREATE TABLE patients_survey (patient_id INT, survey_id INT, feedback VARCHAR(255));
CREATE TABLE survey_questions (question_id INT, question_text VARCHAR(255));
CREATE TABLE survey_responses (response_id INT, patient_id INT, question_id INT, response_text VARCHAR(255));
CREATE TABLE patient_records (record_id INT, patient_id INT, record_date DATE, diagnosis VARCHAR(255));
CREATE TABLE care_quality_metrics (metric_id INT, record_id INT, metric_name VARCHAR(255), metric_value DECIMAL);
